        "yaxisname": "Transaction Count",
        "rotatelabels": "1",
        "setadaptiveymin": "1",
        "theme": "fusion"
      },
      "data": this.state.dataForLine
    };

    const chartConfigsLine ={
      type: 'line',
      width: 600,
      height: 400,
      dataFormat: 'json',
      dataSource: lineChartData,
    };

    return (
      <div>
        <div className="wrapper">
          <div className="main">
            <div className="container">
              <div className="row">
                <div className="col-xs-5 title-container">
                  <Titles />
                </div>
                <div className="col-xs-7 form-container">
                  <Form getWeather={this.getWeather} />
                  <div id='firstTable'>
                    <table >
                      <tr>
                        <th>UserId</th>
                        <th>Currency-From</th>
                        <th>Currency-To</th>
                        <th>Amount-Buy</th>
                        <th>Amount-Sell</th>
                        <th>Time-Placed</th>
                        <th>Originating-Country</th>
                      </tr>
                      {this.state.messages.map(
                        (message) => {return (
                          <tr>
                            <td>{message.userId}</td>
                            <td>{message.currencyFrom}</td>
                            <td>{message.currencyTo}</td>
                            <td>{message.amountBuy}</td>
                            <td>{message.amountSell}</td>
                            <td>{message.timePlaced}</td>
                            <td>{message.originatingCountry}</td>
                          </tr>
                        )}
                      )}
                    </table>
                  </div>
                  <div>
                    <div>
                      <WindowForm getWindowValues={this.getWindowValues}/>
                      <div>
                        <div id='secondTable'>
                          <table >
                            <tr>
                              <th>UserId</th>
                              <th>Currency-From</th>
                              <th>Currency-To</th>
                              <th>Amount-Buy</th>
                              <th>Amount-Sell</th>
                              <th>Time-Placed</th>
                              <th>Originating-Country</th>
                            </tr>
                            {this.state.messagesWindow.map(
                              (message) => {return (
                                <tr>
                                  <td>{message.userId}</td>
                                  <td>{message.currencyFrom}</td>
                                  <td>{message.currencyTo}</td>
